<style lang="scss">
    .check {
		@media (min-width: 810px){
        width: 40vw;
        height: 6vh;
        }
        #Sun{
            animation: forwards cloudMoveback 500ms;
        }
        &:hover #Sun{
            animation: forwards cloudMove 500ms;
        }
        #Cloud{
            animation: forwards cloudMoveback 500ms;
        }
        &:hover #Cloud{
            animation: forwards cloudMove 500ms;
        }
        #The\ tree {
            animation: forwards treeMoveback 500ms;
        }
        &:hover #The\ tree{
            animation: forwards treeMove 500ms;
        }
    }
    @keyframes treeMoveback {
        0% {
            transform: translateX(-30px);
        }
        100% {
            transform: translateX(0px);
        }
    }
    @keyframes treeMove {
        0% {
            transform: translateX(0px);
        }
        100% {
            transform: translateX(-30px);
        }
    }
    @keyframes cloudMoveback {
        0% {
            transform: translateY(10px);
        }
        100% {
            transform: translateX(0px);
        }
    }
    @keyframes cloudMove{
        0% {
            transform: translateX(0px);
        }
        100% {
            transform: translateY(10px);
        }
    }
    .btn {
        width:fit-content;
        height:fit-content;
        border-color: none;
        border-style: none;
        background-color: transparent;
        margin-left: $margin-question;
		@media (min-width: 810px){
            margin-left: 30vw;
        }
    }
</style>